import Layout from '../components/Layout';
import axios from 'axios';

class Treatment extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            location: '',
            symptom: ''
        };

        this.handleLocationChange = this.handleLocationChange.bind(this);
        this.handleSymptomChange = this.handleSymptomChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleLocationChange(event) {
        this.setState({location: event.target.location});
    }

    handleSymptomChange(event) {
        this.setState({symptom: event.target.symptom});
    }

    handleSubmit(event) {
        alert('지역:'  + this.state.location + '\n증상: ' + this.state.symptom);
        event.preventDefault();
    }

    render() {
        return (
            <Layout>
                <form onSubmit={this.handleSubmit}>
                    <label>
                        지역:
                        <input type="text" value={this.state.location} onChange={this.handleLocationChange} />
                    </label>
                    <label>
                        증상:
                        <select value={this.state.symptom} onchange={this.handleSymptomChange}>
                            <option value="1">잎이 노랗게 변함</option>
                            <option value="2">나무 껍질이 벗겨짐</option>
                            <option value="3">나뭇잎에 구멍이 남</option>
                        </select>
                    </label>
                    <input type="submit" value="Submit" />
                </form>
            </Layout>
        )
    }


}
export default Treatment;
