import Layout from '../components/Layout';
import axios from 'axios';
import accounts from '../components/Accounts'

const API_URL = "http://localhost:8080/";

class SignUp extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            id: '',
            pw: '',
            name: '',
            role: ''
        };

        this.handleIdChange = this.handleIdChange.bind(this);
        this.handlePwChange = this.handleIdChange.bind(this);
        this.handleNameChange = this.handleNameChange.bind(this);
        this.handleRoleChange = this.handleRoleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleIdChange(event) {
        this.setState({id: event.target.value});
    }

    handlePwChange(event) {
        this.setState({pw: event.target.value});
    }

    handleNameChange(event) {
        this.setState({name: event.target.value});
    }

    handleRoleChange(event) {
        this.setState({role: event.target.value});
    }

    handleSubmit(event) {
        const formData = new FormData();
        formData.append('Id', this.state.id);
        formData.append('Name', this.state.name);
        formData.append('Role', this.state.role);

        axios.post(API_URL + 'adduser', formData)
        .then(res => {
            alert("가입이 완료되었습니다.")
            accounts.push({'id': this.state.id, 'pw': this.state.pw});
        })
        .catch(err => {
            alert(err);
        })

        event.preventDefault();
    }

    render() {
        return (
            <Layout>
                <form onSubmit={this.handleSubmit}>
                    <label>
                        ID:
                        <input type="text" value={this.state.id} onChange={this.handleIdChange} />
                    </label>
                    <br />
                    <label>
                        PW:
                        <input type="text" value={this.state.pw} onChange={this.handlePwChange} />
                    </label>
                    <br />
                    
                </form>
            </Layout>
        )
    }
}

export default SignUp;
