import Layout from '../components/Layout';
import axios from 'axios';

const TREATMENTURL = '';

class Treatment extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            location: '',
            symptom: ''
        };

        this.handleLocationChange = this.handleLocationChange.bind(this);
        this.handleSymptomChange = this.handleSymptomChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleLocationChange(event) {
        this.setState({location: event.target.value});
    }

    handleSymptomChange(event) {
        this.setState({symptom: event.target.value});
    }

    handleSubmit(event) {
        alert('지역:'  + this.state.location + '\n증상: ' + this.state.symptom);
        const formData = new FormData();
        formData.append('location', this.state.location);
        formData.append('symptom', this.state.symptom);

        axios.post(TREATMENTURL, formData)
        .then(res => {
            alert(res);
        })
        .catch(err => {
            alert(err);
        })
        event.preventDefault();
    }

    render() {
        return (
            <Layout>
                <form onSubmit={this.handleSubmit}>
                    <label>
                        지역:
                        <input type="text" value={this.state.location} onChange={this.handleLocationChange} />
                    </label>
                    <br />
                    <label>
                        증상:
                        <select value={this.state.symptom} onChange={this.handleSymptomChange}>
                            <option value="잎이 노랗게 변함">잎이 노랗게 변함</option>
                            <option value="나무 껍질이 벗겨짐">나무 껍질이 벗겨짐</option>
                            <option value="나뭇잎에 구멍이 남">나뭇잎에 구멍이 남</option>
                        </select>
                    </label>
                    <input type="submit" value="Submit" />
                </form>
            </Layout>
        )
    }


}
export default Treatment;
